    private void multiplyButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_multiplyButtonActionPerformed
        // get first number
        String firstNumberinput = firstInput.getText();
        //convert String into double
        double firstNumber = Double.parseDouble(firstNumberinput);
        //get second number
        String secondNumberinput = secondInput.getText();
        //convert String into double
        double secondNumber = Double.parseDouble(secondNumberinput);
        //do the math - multiply
        double multiDouble = firstNumber * secondNumber;
        
        String multi = Double.toString(multiDouble);
        //output to screen
        output.setText( multi );
    }//GEN-LAST:event_multiplyButtonActionPerformed

