    private void addButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addButtonActionPerformed
        // get first number
        String firstNumberinput = firstInput.getText();
        //convert String into double
        double firstNumber = Double.parseDouble(firstNumberinput);
        //get second number
        String secondNumberinput = secondInput.getText();
        //convert String into double
        double secondNumber = Double.parseDouble(secondNumberinput);
        //do the math - add
        double addDouble = firstNumber + secondNumber;
        
        String add = Double.toString(addDouble);
        //output to screen
        output.setText( add );
    }//GEN-LAST:event_addButtonActionPerformed

    private void subtractButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_subtractButtonActionPerformed
        // get first number
        String firstNumberinput = firstInput.getText();
        //convert String into double
        double firstNumber = Double.parseDouble(firstNumberinput);
        //get second number
        String secondNumberinput = secondInput.getText();
        //convert String into double
        double secondNumber = Double.parseDouble(secondNumberinput);
        //do the math - subtract
        double subtractDouble = firstNumber - secondNumber;
        
        String subtract = Double.toString(subtractDouble);
        //output to screen
        output.setText( subtract );
    }//GEN-LAST:event_subtractButtonActionPerformed

    private void divideButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divideButtonActionPerformed
        // get first number
        String firstNumberinput = firstInput.getText();
        //convert String into double
        double firstNumber = Double.parseDouble(firstNumberinput);
        //get second number
        String secondNumberinput = secondInput.getText();
        //convert String into double
        double secondNumber = Double.parseDouble(secondNumberinput);
        
        double divideDouble = firstNumber / secondNumber;
        
        String divide = Double.toString(divideDouble);
        //output to screen
        output.setText( divide );
    }//GEN-LAST:event_divideButtonActionPerformed

